import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import MerchantAccountBackground from '../../images/backgrounds/merchant-account.png';
import IcoMerchantAccount from '../../images/icons/ico-13.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';

const sectionStyle = {
    width: "100wv",
    margin:"45px 0",
    overflow: "hidden",
    position: "relative",
    "@media (max-width: 800px)": {
        margin:"25px 0",
    },
}

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
};

const flexStyle2 = {
    position: "relative",
    width: "80%",
    height: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
    opacity: "0.95"
};

const linesRight = {
    background: "url(" + LinesRight + ")",
    backgroundPosition: "left -800px bottom -260px",
    backgroundRepeat: "no-repeat",
    width: "100%",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
    // opacity: "0.05"
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-start",
    display: "flex",
    marginTop: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const headerStyle = {
    justifyContent: "flex-start",
    alignItems: "center !important",
    display: "flex",
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const bankTransfersBackground = {
    background: "url(" + MerchantAccountBackground + ")",
    backgroundPosition: "left 0px top 40px",
    backgroundRepeat: "no-repeat",
    //backgroundSize: "contain",
    marginLeft: "300px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "420px"
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
    zIndex: 10
};

class HomeSectionMerchantAccount extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <div style={wrapperStyle}>
                <div style={flexStyle}>
                    <div style={linesRight}>
                        <div style={flexStyle2}>
                            <div style={bankTransfersBackground}>
                            </div>
                            <div>
                                <div style={contentStyle}>
                                    <div style={counterStyle}>
                                        04.
                                    </div>
                                    <div style={headerStyle}>
                                        <img src={IcoMerchantAccount} alt="logo"/>
                                        <p style={headerTextStyle}>
                                            {this.props.t('mainpage.merchant_account')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style = {textGreySubheaderStyle}>
                                        {this.props.t('mainpage.merchant_account_title')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style = {textNormalStyle}>
                                            {this.props.t('mainpage.merchant_account_desc')}
                                        </p>
                                    </div>
                                    <ReadMoreBtn
                                     className="orange_link"
                                     style={linkContainer}
                                     link="merchant_account"
                                     >
                                        {this.props.t('mainpage.btn_more')}
                                    </ReadMoreBtn>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withTranslation()(HomeSectionMerchantAccount)
