import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import PaymentTerminalsBackground from '../../images/backgrounds/payment-terminals.png';
import IcoPaymentTerminals from '../../images/icons/ico-18.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";
import {backgroundLeft, containerStyle, sectionStyle} from "./styles/PublicStyle";

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#ebf7f8",
};

const linesRight = {
    background: "url(" + LinesRight + ")",
    backgroundPosition: "left -800px bottom -180px",
    backgroundRepeat: "no-repeat",
    width: "100%",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textGreySubheaderStyle = {
    // paddingBottom: "1.5rem",
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-start",
    display: "flex",
    marginTop: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const headerStyle = {
    justifyContent: "flex-start",
    alignItems: "center !important",
    display: "flex",
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const bankTransfersBackground = {
    background: "url(" + PaymentTerminalsBackground + ")",
    backgroundPosition: "left 260px top 40px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "208px 336px",
    marginLeft: "300px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "420px"
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

class HomeSectionPaymentTerminals extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundLeft}></div>
                <div style={containerStyle}>
                    <div style={linesRight}></div>
                    <div style={bankTransfersBackground}>
                    </div>
                    <div>
                        <div style={contentStyle}>
                            <div style={counterStyle}>
                                08.
                            </div>
                            <div style={headerStyle}>
                                <img src={IcoPaymentTerminals} alt="logo"/>
                                <p style={headerTextStyle}>
                                    {this.props.t('mainpage.payment_terminals')}
                                </p>
                            </div>
                            <div>
                                <p style={textGreySubheaderStyle}>
                                    {this.props.t('mainpage.payment_terminals_title')}
                                </p>
                            </div>
                            <div>
                                <p style={textNormalStyle}>
                                    {this.props.t('mainpage.payment_terminals_desc')}
                                </p>
                            </div>
                            <ReadMoreBtn
                                className="orange_link"
                                style={linkContainer}
                                link="payments_terminal"
                            >
                                {this.props.t('mainpage.btn_more')}
                            </ReadMoreBtn>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

export default withTranslation()(Radium(HomeSectionPaymentTerminals))
