import '../../../App.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import DonationBackground from '../../images/backgrounds/xx.png';
import IcoDonation from '../../images/icons/ico-21.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
};

const flexStyle2 = {
    position: "relative",
    width: "80%",
    height: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
    opacity: "0.95"
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
    zIndex: 10
};

const linesRight = {
    background: "url(" + LinesRight + ")",
    backgroundPosition: "left -780px bottom 60px",
    backgroundRepeat: "no-repeat",
    width: "100%",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    //opacity: "0.9"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-start",
    display: "flex",
    marginTop: "40px",
    marginBottom: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold",
    marginLeft: "-6px"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const headerStyle = {
    justifyContent: "flex-start",
    alignItems: "center !important",
    display: "flex",
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const textNormalStyle2 = {
    opacity: "0.7",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0px",
    paddingBottom: "0px",
    marginTop: "0px",
    marginBottom: "0px",
};

const donationBackground = {
    background: "url(" + DonationBackground + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "477px 445px",
    marginLeft: "300px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "620px"
};

class HomeSectionDonation extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <div style={wrapperStyle}>
                <div style={flexStyle}>
                    <div style={linesRight}>
                        <div style={flexStyle2}>

                            <div style={donationBackground}>
                            </div>
                            <div>
                                <div style={contentStyle}>
                                    <div style={counterStyle}>
                                        12.
                                    </div>
                                    <div style={headerStyle}>
                                        <img src={IcoDonation} alt="logo"/>
                                        <p style={headerTextStyle}>
                                            {this.props.t('mainpage.donation')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style = {textGreySubheaderStyle}>
                                            {this.props.t('mainpage.donation_title')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style = {textNormalStyle}>
                                            {this.props.t('mainpage.donation_point1')}
                                        </p>
                                        <p style = {textNormalStyle2}>
                                            {this.props.t('mainpage.donation_point2')}
                                        </p>                                        
                                        <p style = {textNormalStyle}>
                                            {this.props.t('mainpage.donation_point3')}
                                        </p>
                                    </div>
                                    <ReadMoreBtn                                    
                                        className="orange_link"
                                        style={linkContainer}
                                        link="donations"
                                    >
                                        {this.props.t('mainpage.btn_more')}
                                    </ReadMoreBtn>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}

export default  withTranslation()(Radium(HomeSectionDonation))
