import React from 'react';
import BodyBackground from '../../images/body_back.png';
import Lines from '../../images/lines.png';
import AnimateCC from "react-adobe-animate";
import SignupButtonLarge from "../../buttons/SignupButtonLarge";
import { withTranslation } from 'react-i18next';


const headerContent = {
    maxWidth: "652px",
}


const wrapperStyle = {
    maxWidth: "1900px",
    margin: "0 auto",
    position: "relative",
    minHeight: "800px",
    display: "flex",
    background: "url(" + Lines + ")",
    backgroundPosition: "left -330px bottom -220px",
    backgroundRepeat: "no-repeat",
};

const animHolderStyle = {
    background: "url(" + BodyBackground + ")",
    minHeight: "1008px",
    minWidth: "1008px",
    backgroundRepeat: "no-repeat",
    position: "relative",
    marginLeft: "38%",
    backgroundPosition: "right -38px top",
    top: "-210px"
};

const headerContentStyle = {
    position: "absolute",
    textAlign: "left",
    maxWidth: "1140px",
    minWidth: "1140px",
    zIndex: "10",
    marginTop: "81px",
    justifyContent: "left",
    display: "flex",
    paddingLeft: "15px",
    paddingRight: "15px",
};


const animStyle = {
    marginTop: "138px",
    marginLeft: "200px",
};

const textOrangeStyle = {
    textTransform: "uppercase",
    paddingBottom: "1.5rem",
    paddingTop: "1.4rem",
    color: "#FF9B0F",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif'
};

const textHeaderStyle = {
    paddingBottom: "0",
    paddingTop: "0",
    color: "#363636",
    fontSize: "2.5rem",
    fontFamily: 'ProximaNova, sans-serif',
    // fontWeight: '700 !important',
    fontWeight: 'bold',
    marginTop: "0",
    marginBottom: "0.5rem",
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "1.5rem",
    paddingBottom: "1.5rem",
};

class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            paused: false
        };
        this.handleLoginRedirect = this.handleLoginRedirect.bind(this);
        this.handleRegisterRedirect = this.handleRegisterRedirect.bind(this);
    }

    componentDidMount() {
        setTimeout(function () {
            this.setState({paused: true});
        }.bind(this), 3700);
    };

    handleLoginRedirect() {
        this.props.loginRedirectHandler();
    };

    handleRegisterRedirect() {
        this.props.registerRedirectHandler();
    };

    render() {
        return (
            <div style={wrapperStyle}>
                <div style={headerContentStyle}>
                    <div style={headerContent}>
                        <div style={textOrangeStyle}>
                            {this.props.t('mainpage.banking_service')}
                        </div>
                        <h1 style={textHeaderStyle}>
                            PAYSUNION – {this.props.t('mainpage.banking_intro')}
                        </h1>
                        <p style={textNormalStyle}>
                            {this.props.t('mainpage.banking_intro_extra')}
                        </p>

                        <SignupButtonLarge
                            registerRedirectHandler={this.handleRegisterRedirect}
                        />
                    </div>
                </div>

                <div style={animHolderStyle}>
                    <div style={animStyle}>
                        <AnimateCC
                            animationName="anim1"
                            paused={this.state.paused}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
export default withTranslation()(Home)
