import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import StockBackground from '../../images/backgrounds/stock.png';
import IcoStockSafe from '../../images/icons/ico-21.png';
import IcoFood from '../../images/icons/ico-22.png';
import IcoCoffee from '../../images/icons/ico-23.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
    minHeight: "500px"
};

const flexStyle2 = {
    position: "relative",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#2b3241",
    opacity: "0.95"
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

const linesRight = {
    background: "url(" + LinesRight + ")",
    backgroundPosition: "left -1000px bottom -260px",
    backgroundRepeat: "no-repeat",
    width: "100%",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex"
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textSubheaderHolder = {
    position: 'relative',
    zIndex: "10",
    backgroundColor: "#2b3241",
    maxWidth: "520px",
    marginLeft: "-10px"
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    marginBottom: "0px",
    paddingLeft: "10px"
};

const textNormalStyle = {
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    marginTop: "10px",
    paddingBottom: "0.5rem",
    backgroundColor: "#2b3241",
    opacity: "0.8",
    paddingLeft: "10px"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-start",
    display: "flex",
    marginTop: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold",
    marginLeft: "-6px"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const headerStyle = {
    justifyContent: "flex-start",
    alignItems: "center !important",
    display: "flex",
};

const examplesStyle = {    
    display: "flex",
    justifyContent: "flex-start",
    alignItems: "center",
    marginTop: "20px"
};

const exampleYellowHeader = {
    color: "#FF9B0F",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
};



const stockBackground = {
    background: "url(" + StockBackground + ")",
    backgroundPosition: "left 0px top 160px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "354px 393px",
    marginLeft: "300px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "600px"
};

const squareBracketStyle = {
    border: "5px solid #fff",
    height: "46px",
    width: "200px",
    position: "absolute",
    zIndex: "1",
    marginLeft: "-20px",
    opacity: "0.05",
    top: '10px',
    left: '5px'
};

const squareBracketStyleInner = {
    position: 'absolute',
    top: '-5px',
    bottom: '-5px',
    right: '-5px',
    width: '95%',
    background: '#2b3241'
}

const icoStyle = {
    display: "block",
    maxWidth: "64px",
    maxHeight: "64px",
    minWidth: "64px",
    minHeight: "64px",
};

const exampleTextStyle = {
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    margin: 0,
    paddingBottom: "0",
    backgroundColor: "#2b3241",
    opacity: "0.9",
    paddingLeft: "16px"
};

class HomeSectionStock extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <div style={wrapperStyle}>
                <div style={flexStyle}>
                    <div style={linesRight}>
                        <div style={flexStyle2}>

                            <div style={stockBackground}>
                            </div>
                            <div>
                                <div style={contentStyle}>
                                    <div style={counterStyle}>
                                        10.
                                    </div>
                                    <div style={headerStyle}>
                                        <img src={IcoStockSafe} alt="logo"/>
                                        <p style={headerTextStyle}>
                                            {this.props.t('mainpage.stock')}
                                        </p>
                                    </div>                                    
                                    <div style = {textSubheaderHolder}>
                                        <div style={squareBracketStyle}>
                                            <span style={squareBracketStyleInner}></span>
                                        </div>
                                        <p style = {textGreySubheaderStyle}>
                                            {this.props.t('mainpage.stock_title')}
                                        </p>
                                        <p style={textNormalStyle}>
                                            {this.props.t('mainpage.stock_desc')}
                                        </p>
                                    </div>
                                    <div style={exampleYellowHeader}>
                                        <p>
                                            {this.props.t('mainpage.stock_example')}
                                        </p>
                                    </div>

                                    <div style={examplesStyle}>
                                        <img src={IcoFood} alt="logo" style={icoStyle}/>
                                        <p style={exampleTextStyle}>
                                            {this.props.t('mainpage.stock_example_point1')}
                                        </p>
                                    </div>
                                    <div style={examplesStyle}>
                                        <img src={IcoCoffee} alt="logo" style={icoStyle}/>
                                        <p style={exampleTextStyle}>
                                            {this.props.t('mainpage.stock_example_point2')}
                                        </p>
                                    </div>
                                    <ReadMoreBtn link={'stock'} style={linkContainer}>{this.props.t('mainpage.btn_more')}</ReadMoreBtn>
                                    {/* <div
                                        className="orange_link"
                                        style={linkContainer}>
                                        <a
                                            className="orange_link"
                                            href="/stock"
                                            style={{display: "flex", flexDirection:"row", alignItems: 'center'}}
                                        >
                                                {this.props.t('mainpage.btn_more')}

                                            <div className="arrow-min" style={{marginTop: '0'}} />
                                        </a>
                                    </div> */}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}

export default withTranslation()(HomeSectionStock)
