import '../../../App.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import DonationBackground from '../../images/backgrounds/xx.png';
import IcoDonation from '../../images/icons/ico-21.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";
import {
    backgroundBlack,
    containerStyle,
    headerStyle,
    headerTextStyleWhite,
    sectionStyle,
    textNormalStyleWhite
} from "./styles/PublicStyle";

const linkContainer = {
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
    zIndex: 10
};

const contentStyle = {
    maxWidth: "590px",
    padding:"50px 0",
    marginRight: "80px",
    marginLeft: "auto",
    position:"relative",
    "@media (max-width: 800px)": {
        maxWidth: "none",
        padding:"20px 0",
        paddingBottom: "40px",
        marginRight: "0px",
    },
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#fff",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
};

const counterStyle = {
    fontSize: "70px",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold",
    marginLeft: "-6px"
};

const donationBackground = {
    background: "url(" + DonationBackground + ")",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    width: "560px",
    height: "386px",
    position: "absolute",
    top: "50%",
    marginTop: "-193px",
    left: "50%",
    marginLeft: "-740px",
    "@media (max-width: 995px)": {
        display: "none",
    },
};

class HomeSectionDonation extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundBlack}></div>
                <div style={containerStyle}>
                    <div style={donationBackground}>
                    </div>
                    <div style={contentStyle}>
                        <div style={counterStyle}>
                            12.
                        </div>
                        <div style={headerStyle}>
                            <img src={IcoDonation} alt="logo"/>
                            <p style={headerTextStyleWhite}>
                                {this.props.t('mainpage.donation')}
                            </p>
                        </div>
                        <div>
                            <p style={textGreySubheaderStyle}>
                                {this.props.t('mainpage.donation_title')}
                            </p>
                        </div>
                        <div>
                            <p style={textNormalStyleWhite}>
                                {this.props.t('mainpage.donation_point1')}
                            </p>
                            <p style={textNormalStyleWhite}>
                                {this.props.t('mainpage.donation_point2')}
                            </p>
                            <p style={textNormalStyleWhite}>
                                {this.props.t('mainpage.donation_point3')}
                            </p>
                        </div>
                        <ReadMoreBtn
                            className="orange_link"
                            style={linkContainer}
                            link="donations"
                        >
                            {this.props.t('mainpage.btn_more')}
                        </ReadMoreBtn>
                    </div>
                </div>
            </section>
        )
    }
}

export default  withTranslation()(Radium(HomeSectionDonation))
