import '../../../App.css';
import React from 'react';
import LinesLeft from '../../images/lines-left.png';
import PersonalAccountBackground from '../../images/backgrounds/personal-account.png';
import IcoInternetAcquiring from '../../images/icons/ico-7.png';
import '../../css/orange-links.css';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import {backgroundLeft, containerStyle} from "./styles/PublicStyle";

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "50px 0px 0px 50px",
    backgroundColor: "#ebf7f8",
};

const linesLeft = {
    background: "url(" + LinesLeft + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textGreySubheaderStyle = {
    // paddingBottom: "1.5rem",
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-end",
    display: "flex",
    marginTop: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const headerStyle = {
    justifyContent: "flex-start",
    alignItems: "center !important",
    display: "flex",
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const personalAccountBackground = {
    background: "url(" + PersonalAccountBackground + ")",
    backgroundPosition: "left 0px top 40px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "contain",
    marginLeft: "100px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "420px"
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

class HomeSectionInternetAcquiring extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundLeft}></div>
                <div style={containerStyle}>
                    <div style={linesLeft}>
                        <div style={contentStyle}>
                            <div style={counterStyle}>
                                05.
                            </div>
                            <div style={headerStyle}>
                                <img src={IcoInternetAcquiring} alt="logo"/>
                                <p style={headerTextStyle}>
                                    {this.props.t('mainpage.internet_acquiring')}
                                </p>
                            </div>
                            <div>
                                <p style = {textGreySubheaderStyle}>
                                    {this.props.t('mainpage.internet_acquiring_title')}
                                </p>
                            </div>
                            <div>
                                <p style = {textNormalStyle}>
                                    {this.props.t('mainpage.internet_acquiring_desc')}
                                </p>
                            </div>
                            <ReadMoreBtn
                                className="orange_link"
                                style={linkContainer}
                                link="internet_acquiring"
                            >
                                {this.props.t('mainpage.btn_more')}
                            </ReadMoreBtn>
                        </div>
                    </div>
                    <div style={personalAccountBackground}>
                    </div>
                </div>
            </section>
        )
    }
}
export default withTranslation()(HomeSectionInternetAcquiring)
