import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesLeft from '../../images/lines-left.png';
import PaymentProcessingBackground from '../../images/backgrounds/payment-processing.png';
import IcoPaymentProcessing from '../../images/icons/ico-20.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import {
    backgroundLeft,
    backgroundRight,
    containerStyle,
    headerStyle,
    sectionStyle,
    textNormalStyle
} from "./styles/PublicStyle";

const linkContainer = {
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

const linesLeft = {
    background: "url(" + LinesLeft + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
};

const contentStyle = {
    maxWidth: "585px",
    padding:"50px 0",
    marginRight: "80px",
    marginLeft: "auto",
    position:"relative",
    "@media (max-width: 800px)": {
        maxWidth: "none",
        padding:"20px 0",
        paddingBottom: "40px",
        marginRight: "0px",
    },
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const headerTextStyle = {
    fontSize: "2rem",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "10px",
    marginBottom: "0",
};

const personalAccountBackground = {
    background: "url(" + PaymentProcessingBackground + ")",
    backgroundPosition: "left 0px top 40px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "402px 377px",
    marginLeft: "100px",
    minWidth: "500px",
    maxWidth: "600px",
    maxHeight: "420px"
};

class HomeSectionPaymentProcessing extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundRight}></div>
                <div style={containerStyle}>
                    <div style={linesLeft}></div>
                        <div style={contentStyle}>
                            <div style={counterStyle}>
                                07.
                            </div>
                            <div style={headerStyle}>
                                <img src={IcoPaymentProcessing} alt="logo"/>
                                <p style={headerTextStyle}>                                    
                                    {this.props.t('mainpage.payment_processing')}
                                </p>
                            </div>
                            <div>
                                <p style = {textGreySubheaderStyle}>
                                    {this.props.t('mainpage.payment_processing_title')}
                                </p>
                            </div>
                            <div>
                                <p style = {textNormalStyle}>
                                    {this.props.t('mainpage.payment_processing_desc')}
                                </p>
                                <p style = {textNormalStyle}>
                                    {this.props.t('mainpage.payment_processing_desc2')}
                                </p>
                            </div>
                            <ReadMoreBtn
                                className="orange_link"
                                style={linkContainer}
                                link="payment_processing"
                            >
                                {this.props.t('mainpage.btn_more')}
                            </ReadMoreBtn>
                        </div>

                    <div style={personalAccountBackground}>
                    </div>
                </div>
            </section>
        )
    }
}

export default withTranslation()(HomeSectionPaymentProcessing)
