import '../../../App.css';
import React from 'react';
import AppBarHome from "./AppBarHome";
import HomeFooter from "./HomeFooter";
import { Redirect } from 'react-router-dom';
import BodyBackground from '../../images/body_back.png';
import AnimateCC from "react-adobe-animate";
import IcoDonation from '../../images/icons/ico-21.png';
import { withTranslation } from 'react-i18next';


const headerContent = {
    maxWidth: "600px",
};

const wrapperStyle = {
    position: "relative",
    height: "600px",
    justifyContent: "space-between",
    display: "flex",
    backgroundPosition: "left 330px bottom -220px",
    backgroundRepeat: "no-repeat",
};

const animHolderStyle = {
    background: "url(" + BodyBackground + ")",
    minHeight: "1008px",
    minWidth: "1008px",
    backgroundRepeat: "no-repeat",
    position: "relative",
    marginLeft: "47%",
    backgroundPosition: "right -38px top",
    top: "-277px"
};

const animStyle = {
    marginTop: "20%",
    marginLeft: "10%",
    maxWidth: "512px",
    maxHeight: "512px",
    background: "transparent"
};

const headerContentStyle = {
    position: "absolute",
    textAlign: "left",
    maxWidth: "1140px",
    minWidth: "1140px",
    zIndex: "10",
    marginTop: "20px",
    justifyContent: "left",
    display: "flex",
    paddingLeft: "70px",
    paddingRight: "15px",
};

const textHeaderStyle = {
    paddingBottom: "0",
    paddingTop: "0",
    color: "#363636",
    fontSize: "2.5rem",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '500',
    marginTop: "0",
    marginBottom: "0.5rem",
    lineHeight: "32px"
};

const textSubHeaderStyle = {
    paddingBottom: "0",
    paddingTop: "0",
    color: "#363636",
    fontSize: "1.25rem",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '500',
    marginTop: "0",
    marginBottom: "1.8rem",
};

const textNormalStyle = {
    opacity: "1",
    // color: "#363636",
    color: "black",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '400 !important',
    marginTop: 0,
    marginBottom: 20,
    lineHeight: "1.5"
};

const divHolderStyle = {
    display: "flex",
    justifyContent: "flex-start",
};

const headerDivItemLeft = {

};
const headerDivItemRight = {
    marginLeft: "40px"
};

class StaticPageDonations extends React.Component {

    constructor(props) {
        super(props);
        this.state= {
            paused: false,
            login_redirect: false,
            register_redirect: false
        };
        this.handleLoginRedirect = this.handleLoginRedirect.bind(this);
        this.handleRegisterRedirect = this.handleRegisterRedirect.bind(this);
    }

    componentDidMount() {
        setTimeout(function(){
            this.setState({paused:true});
        }.bind(this), 3700);
    }

    handleLoginRedirect() {
        this.setState({login_redirect: true});
    };

    handleRegisterRedirect() {
        this.setState({register_redirect: true});
    };


    render() {
        const {t} = this.props;
        if (
            !this.state.login_redirect &&
            !this.state.register_redirect
        ) {
            return (

                <div className="App">
                        <header className="App-header">
                            <AppBarHome
                                loginRedirectHandler={this.handleLoginRedirect}
                                registerRedirectHandler={this.handleRegisterRedirect}
                            />
                        </header>
                        <div style={wrapperStyle}>
                            <div style={headerContentStyle}>
                                <div style={headerContent}>
                                    <div style={divHolderStyle}>
                                        <div style={headerDivItemLeft}>
                                            <img src={IcoDonation} alt="logo"/>
                                        </div>
                                        <div style={headerDivItemRight}>
                                            <h1 style={textHeaderStyle}>
                                                {t('donations.title')}
                                            </h1>
                                            <h1 style={textSubHeaderStyle}>
                                                {t('donations.desc')}
                                            </h1>
                                        </div>
                                    </div>
                                    <p style={textNormalStyle}>
                                        {t('donations.sub_item_1')}
                                    </p>
                                    <p style={textNormalStyle}>
                                        {t('donations.sub_item_2')}
                                    </p>
                                    <p style={textNormalStyle}>
                                        {t('donations.sub_item_3')}
                                    </p>
                                    <p style={textNormalStyle}>
                                        {t('donations.sub_item_4')}
                                    </p>
                                </div>
                            </div>

                            <div style={animHolderStyle}>
                                <div style={animStyle}>
                                    <AnimateCC
                                        animationName="anim13"
                                        paused={this.state.paused}
                                    />
                                </div>
                            </div>
                        </div>

                        <div style={{zIndex: 50, position: "relative"}}>
                            <HomeFooter />
                        </div>
                </div>
            );
        }
        else if (this.state.login_redirect && !this.state.register_redirect) {
            return <Redirect push to="/login"/>;
        }
        else if (this.state.register_redirect && !this.state.login_redirect) {
            return <Redirect push to="/register"/>;
        }

    }
}

export default withTranslation()(StaticPageDonations)
