import LinesRight from "../../../images/lines-right-v2.png";
import lineLeft from "../../../images/backgrounds/line-left.png";

export const containerStyle = {
    maxWidth: "1190px",
    margin: "0 auto",
    padding: "0px 32px",
    "@media (max-width: 800px)": {
        padding: "0px 25px",
    },
}

export const headerTextStyle = {
    fontSize: "36px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "0",
    marginBottom: "0",
    "@media (max-width: 425px)": {
        fontSize: "32px",
        marginLeft: "0px",
    },
};

export const headerTextStyleWhite = {
    fontSize: "36px",
    color: "#fff",
    fontFamily: 'ProximaNova, sans-serif',
    marginLeft: "25px",
    marginTop: "0",
    marginBottom: "0",
    "@media (max-width: 425px)": {
        fontSize: "32px",
        marginLeft: "0px",
    },
};

export const backgroundRight = {
    width: "100%",
    height: "100%",
    position: "absolute",
    left: "50%",
    marginLeft: "-595px",
    backgroundColor: "#ebf7f8",
    borderRadius: "32px",
    "@media (max-width: 1200px)": {
        left: "0",
        marginLeft: "0px",
    },
    "@media (max-width: 800px)": {
        borderRadius: "0px",
    },
}

export const backgroundLeft = {
    width: "100%",
    height: "100%",
    position: "absolute",
    right: "50%",
    marginRight: "-595px",
    backgroundColor: "#ebf7f8",
    borderRadius: "32px",
    "@media (max-width: 1200px)": {
        right: "0",
        marginRight: "0px",
    },
    "@media (max-width: 800px)": {
        borderRadius: "0px",
    },
}

export const backgroundBlack = {
    width: "100%",
    height: "100%",
    position: "absolute",
    right: "50%",
    marginRight: "-595px",
    background: "url(" + lineLeft + ")",
    backgroundPosition: "right center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "calc(50% - 595px) auto",
    backgroundColor: "#2B3241",
    borderRadius: "32px",
    "@media (max-width: 1200px)": {
        right: "0",
        marginRight: "0px",
    },
    "@media (max-width: 800px)": {
        borderRadius: "0px",
    },
}

export const headerStyle = {
    alignItems: "center",
    display: "flex",
    "@media (max-width: 425px)": {
        display: "block",
    },
};

export const textNormalStyle = {
    opacity: "0.7",
    //color: "#363636",
    fontSize: "16px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

export const textNormalStyleWhite = {
    opacity: "0.7",
    color: "#fff",
    fontSize: "16px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};
