import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesRight from '../../images/lines-right-v2.png';
import BankTransfersBackground from '../../images/backgrounds/bank-transfers-2.png';
import IcoBankTransfers from '../../images/icons/ico-4.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";
import {headerTextStyle, containerStyle, backgroundLeft} from "./styles/PublicStyle";

const sectionStyle = {
    width: "100wv",
    margin:"45px 0",
    overflow: "hidden",
    position: "relative",
    "@media (max-width: 800px)": {
        margin:"25px 0",
    },
}

const flexStyle = {
    width: "100%",
    display: "flex",
    borderRadius: "0px 50px 50px 0px",
    backgroundColor: "#ebf7f8",
};

const linesRight = {
    background: "url(" + LinesRight + ")",
    backgroundPosition: "left -800px bottom -180px",
    backgroundRepeat: "no-repeat",
    width: "100%",
    height: "100%",
    position: "relative",
    justifyContent: "flex-end",
    display: "flex",
    "@media (max-width: 800px)": {
        justifyContent: "flex-start",
    },
};

const contentStyle = {
    padding:"50px 0",
    textAlign: "left",
    maxWidth: "490px",
    "@media (max-width: 800px)": {
        maxWidth: "none",
    },
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-end",
    display: "flex",
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const textNormalStyle = {
    opacity: "0.7",
    //color: "#363636",
    fontSize: "16px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const bankTransfersBackground = {
    width: "480px",
    height: "320px",
    position: "absolute",
    left: "50%",
    marginLeft: "-550px",
    top: "50%",
    marginTop: "-160px",
    background: "url(" + BankTransfersBackground + ")",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    "@media (max-width: 910px)": {
        display: "none",
    },
};

const linkContainer = {
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

class HomeSectionBankTransfers extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundLeft}></div>
                <div style={containerStyle}>
                    <div style={linesRight}>
                        <div style={bankTransfersBackground}>
                        </div>
                        <div>
                            <div style={contentStyle}>
                                <div style={counterStyle}>
                                    02.
                                </div>
                                <div style={ headerStyle }>
                                    <img src={IcoBankTransfers} alt="logo"/>
                                    <p style={ headerTextStyle }>
                                        {this.props.t('mainpage.bank_transfers')}
                                    </p>
                                </div>
                                <div>
                                    <p style={textGreySubheaderStyle}>
                                        {this.props.t('mainpage.bank_transfers_title')}
                                    </p>
                                </div>
                                <div>
                                    <p style={textNormalStyle}>
                                        {this.props.t('mainpage.bank_transfers_desc')}
                                    </p>
                                </div>
                                <ReadMoreBtn
                                    link="bank_transfers"
                                    className="orange_link"
                                    style={linkContainer}
                                >{this.props.t('mainpage.btn_more')}</ReadMoreBtn>
                                {/* <div
                                    className="orange_link"
                                    style={linkContainer}>
                                    <a
                                        className="orange_link"
                                        href="/bank_transfers"
                                        style={{display: "flex", flexDirection:"row"}}
                                    >
                                        {this.props.t('mainpage.btn_more')}
                                        <div className="arrow-min" />
                                    </a>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

export default  withTranslation()(Radium(HomeSectionBankTransfers));
