import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesLeft from '../../images/lines-left.png';
import PersonalAccountBackground from '../../images/backgrounds/personal-account.png';
import IcoPersonalAccount from '../../images/icons/ico-1.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Lines from "../../images/lines.png";
import Radium from "radium";
import {backgroundRight, containerStyle, headerTextStyle} from "./styles/PublicStyle";

const sectionStyle = {
    width: "100wv",
    overflow: "hidden",
    position: "relative",
    marginBottom: "45px",
    "@media (max-width: 800px)": {
        marginBottom:"25px 0",
    },
}

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "50px 0px 0px 50px",
    "@media (max-width: 800px)": {
        width: "100%",
    },
};

const linesLeft = {
    background: "url(" + LinesLeft + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px",
    "@media (max-width: 800px)": {
        marginLeft: "0px",
        marginTop: "10px",
        maxWidth: "none",
    },
};

const textGreySubheaderStyle = {
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8",
    "@media (max-width: 425px)": {
        paddingTop: "0px",
    },
};

const wrapperStyle = {
    position: "relative",
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const headerStyle = {
    alignItems: "center",
    display: "flex",
    "@media (max-width: 425px)": {
        display: "block",
    },
};

const textNormalStyle = {
    opacity: "0.7",
    color: "#363636",
    fontSize: "16px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: '700 !important',
    paddingTop: "0.4rem",
    paddingBottom: "0.5rem",
};

const personalAccountBackground = {
    width: "600px",
    height: "100%",
    position: "absolute",
    left:"50%",
    marginLeft:"150px",
    background: "url(" + PersonalAccountBackground + ")",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    "@media (max-width: 1200px)": {
        display: "none",
    },
};

const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

class HomeSectionPersonalAccount extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={ backgroundRight }></div>
                <div style={ containerStyle }>
                    <div style={wrapperStyle}>
                        <div style={flexStyle}>
                            <div style={linesLeft}>
                                <div style={contentStyle}>
                                    <div style={counterStyle}>
                                        01.
                                    </div>
                                    <div style={headerStyle}>
                                        <img src={IcoPersonalAccount} alt="logo"/>
                                        <p style={ headerTextStyle }>
                                            {this.props.t('mainpage.p_b_account')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style={textGreySubheaderStyle}>
                                            {this.props.t('mainpage.p_b_account_title')}
                                        </p>
                                    </div>
                                    <div>
                                        <p style={textNormalStyle}>
                                            {this.props.t('mainpage.p_b_account_desc')}
                                        </p>
                                    </div>
                                    <ReadMoreBtn
                                        className="orange_link"
                                        style={linkContainer}
                                        link="personal_account">
                                        {this.props.t('mainpage.btn_more')}
                                    </ReadMoreBtn>
                                </div>
                            </div>
                            <div style={personalAccountBackground}>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}
export default  withTranslation()(Radium(HomeSectionPersonalAccount))
