import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesLeft from '../../images/lines-left.png';
import PurpleBankCards from '../../images/backgrounds/personal-bank-cards.png';
import BankCardBackground from '../../images/backgrounds/card-background.png';
import IcoBankCards from '../../images/icons/ico-14.png';
import BankCardStandardButton from "../../buttons/BankCardStandardButton";
import BankCardPremiumButton from "../../buttons/BankCardPremiumButton";
import BankCardMetalButton from "../../buttons/BankCardMetalButton";
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";
import {
    backgroundLeft,
    backgroundRight,
    containerStyle,
    headerStyle,
    headerTextStyle,
    textNormalStyle
} from "./styles/PublicStyle";

const sectionStyle = {
    width: "100wv",
    margin:"45px 0",
    overflow: "hidden",
    position: "relative",
    "@media (max-width: 800px)": {
        margin:"25px 0",
    },
}

const flexStyle = {
    position: "relative",
    width: "80%",
    justifyContent: "flex-start",
    display: "flex",
    borderRadius: "50px 0px 0px 50px",
    backgroundColor: "#ebf7f8",
};

const linesLeft = {
    background: "url(" + LinesLeft + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
    position: "relative",
    width: "100%",
    maxWidth: "660px",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
};

const purpleBankCardsBackground = {
    background: "url(" + PurpleBankCards + ")",
    backgroundPosition: "left 460px bottom -75px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "263px 278px",
    width: "100%",
    height: "100%",
    justifyContent: "flex-start",
    display: "flex",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textCardHeader = {
    width: "400px",
    color: "white",
    fontFamily: 'ProximaNova, sans-serif',
    fontSize: "1.25rem",
    marginTop: "70px",
    marginLeft: "120px"
};

const textCardSubheader = {
    width: "550px",
    color: "white",
    opacity: "0.8",
    fontFamily: 'ProximaNova, sans-serif',
    fontSize: "1rem",
    marginTop: "50px",
    marginLeft: "120px"
};

const textCardNormal = {
    width: "550px",
    color: "white",
    opacity: "0.8",
    fontFamily: 'ProximaNova, sans-serif',
    fontSize: "1rem",
    marginTop: "60px",
    marginLeft: "40px"
};

const wrapperStyle = {
    width: "100%",
    justifyContent: "flex-end",
    display: "flex",
    marginTop: "40px"
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const bankCardBackground = {
    background: "url(" + BankCardBackground + ")",
    backgroundPosition: "left 0px top 40px",
    backgroundRepeat: "no-repeat",
    backgroundSize: "contain",
    marginLeft: "100px",
    minWidth: "640px",
    maxWidth: "640px",
    maxHeight: "420px"
};

const pStyleNoMargin = {
    marginTop: "0",
    marginBottom: "0"
};

const buttonHolderStyle = {
    marginTop: "10px",
    justifyContent: "center",
    display: "flex"
};
const linkContainer = {
    marginBottom: 60,
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

class HomeSectionBankCards extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundRight}></div>
                <div style={containerStyle}>
            <div style={wrapperStyle}>
                <div style={flexStyle}>
                    <div style={purpleBankCardsBackground}>
                        <div style={linesLeft}>
                            <div style={contentStyle}>
                                <div style={counterStyle}>
                                    03.
                                </div>
                                <div style={headerStyle}>
                                    <img src={IcoBankCards} alt="logo"/>
                                    <p style={headerTextStyle}>
                                        {this.props.t('mainpage.personal_bank')}
                                    </p>
                                </div>
                                <div>
                                    <p style = {textNormalStyle}>
                                        {this.props.t('mainpage.personal_bank_desc')}
                                    </p>
                                </div>
                                <ReadMoreBtn
                                link="personal_bank_cards"
                                className="orange_link"
                                style={linkContainer}
                                >
                                    {this.props.t('mainpage.btn_more')}
                                </ReadMoreBtn>
                              
                            </div>
                         </div>
                        <div style={bankCardBackground}>
                            <div>
                                <div style={textCardHeader}>
                                    <p style={pStyleNoMargin} dangerouslySetInnerHTML={{__html: this.props.t('mainpage.personal_bank_card_title')}} />
                                </div>
                                <div style={textCardSubheader}>
                                    <p style={pStyleNoMargin}>                                    
                                        {this.props.t('mainpage.personal_bank_card_type')}
                                    </p>
                                </div>
                                <div style={buttonHolderStyle}>
                                    <BankCardStandardButton />
                                    <BankCardPremiumButton />
                                    <BankCardMetalButton />
                                </div>
                                <div style={textCardNormal}>
                                    <p style={pStyleNoMargin}>
                                        {this.props.t('mainpage.personal_bank_card_footer')}
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
                </div>
            </section>
        )
    }
}

export default withTranslation()(Radium(HomeSectionBankCards))
