import '../../../App.css';
import '../../css/orange-links.css';
import React from 'react';
import LinesLeft from '../../images/lines-left.png';
import POSBackground from '../../images/backgrounds/pos.png';
import IcoPOS from '../../images/icons/ico-25.png';
import { withTranslation } from 'react-i18next';
import ReadMoreBtn from './components/ReadMoreBtn';
import Radium from "radium";
import {
    backgroundLeft,
    backgroundRight,
    containerStyle,
    headerStyle,
    headerTextStyle,
    sectionStyle, textNormalStyle
} from "./styles/PublicStyle";


const linkContainer = {
    marginTop: 20,
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start"
};

const linesLeft = {
    background: "url(" + LinesLeft + ")",
    backgroundPosition: "left 0px top 0px",
    backgroundRepeat: "no-repeat",
};

const contentStyle = {
    marginTop: "50px",
    marginLeft: "90px",
    textAlign: "left",
    maxWidth: "490px"
};

const textGreySubheaderStyle = {
    // paddingBottom: "1.5rem",
    paddingTop: "0.8rem",
    color: "#363636",
    fontSize: "14px",
    fontFamily: 'ProximaNova, sans-serif',
    fontWeight: "bold",
    opacity: "0.8"
};

const counterStyle = {
    fontSize: "70px",
    color: "#363636",
    fontFamily: 'ProximaNova, sans-serif',
    letterSpacing: "-5px",
    opacity: "0.2",
    fontWeight: "bold"
};

const personalAccountBackground = {
    background: "url(" + POSBackground + ")",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    width: "600px",
    height: "406px",
    position: "absolute",
    top: "50%",
    marginTop: "-203px",
    left: "50%",
    marginLeft: "300px",
    "@media (max-width: 995px)": {
        display: "none",
    },
};

class HomeSectionPOS extends React.Component {

    // constructor(props) {
    //     super(props);
    // }

    render() {
        return (
            <section style={sectionStyle}>
                <div style={backgroundRight}></div>
                <div style={containerStyle}>
                    <div style={linesLeft}>
                        <div style={contentStyle}>
                            <div style={counterStyle}>
                                09.
                            </div>
                            <div style={headerStyle}>
                                <img src={IcoPOS} alt="logo"/>
                                <p style={headerTextStyle}>
                                    {this.props.t('mainpage.pos')}
                                </p>
                            </div>
                            <div>
                                <p style = {textGreySubheaderStyle}>
                                    {this.props.t('mainpage.pos_title')}
                                </p>
                            </div>
                            <div>
                                <p style = {textNormalStyle}>
                                    {this.props.t('mainpage.pos_desc')}
                                </p>
                            </div>
                            <ReadMoreBtn 
                                className="orange_link"
                                style={linkContainer}
                                link="pos"
                            >
                                {this.props.t('mainpage.btn_more')}
                            </ReadMoreBtn>                            
                        </div>
                    </div>
                    <div style={personalAccountBackground}>
                    </div>
                </div>
            </section>
        )
    }
}

export default withTranslation()(Radium(HomeSectionPOS))
